CREATE TABLE `p_template` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `template_code` varchar(128) NOT NULL DEFAULT '' COMMENT '模板code，同一工具不同版本的 code相同',
  `template_type` tinyint(4) NOT NULL COMMENT '模板类型，订单条类型:1，订单类型:2，跨订单类型:3',
  `operation` varchar(4000) DEFAULT NULL,
  `name` varchar(64) NOT NULL COMMENT '模板名字',
  `description` varchar(128) NOT NULL COMMENT '模板描述',
  `version` tinyint(4) NOT NULL DEFAULT '1' COMMENT '模板版本',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '状态: 0-不可用 1-可用',
  `creator` varchar(32) NOT NULL DEFAULT '' COMMENT '创建人',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  KEY `idx_code` (`template_code`),
  KEY `idx_create_time` (`create_time`),
  KEY `idx_update_time` (`update_time`)
) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8mb4 COMMENT='促销模板表'  ;



