CREATE TABLE `p_activity` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT BY GROUP,
  `template_id` bigint(11) NOT NULL COMMENT 'p_template表的id',
  `template_code` varchar(128) NOT NULL COMMENT '模板code',
  `template_type` tinyint(4) NOT NULL COMMENT '模板类型',
  `shop_id` bigint(20) NOT NULL DEFAULT '0' COMMENT '店铺id : 0默认值，平台活动',
  `tag_name` varchar(32) NOT NULL DEFAULT '' COMMENT '标签名字',
  `name` varchar(32) NOT NULL COMMENT '活动名称，前端展示用',
  `start_time` datetime NOT NULL COMMENT '优惠活动开始时间',
  `end_time` datetime NOT NULL COMMENT '优惠活动结束时间',
  `channel` tinyint(4) NOT NULL DEFAULT '1' COMMENT '适用渠道：1-环球捕手/斑马 2-本地生活 3-小区乐 4-美食买手',
  `warmup_time` datetime DEFAULT NULL COMMENT '预热时间',
  `descritpion` varchar(128) NOT NULL DEFAULT '' COMMENT '描述（优惠内容）',
  `expression` varchar(32) NOT NULL DEFAULT '' COMMENT '分组表达式，活动参与者是分组时使用，如 #1&!#2&#3',
  `operation_param` varchar(2000) NOT NULL DEFAULT '' COMMENT '活动实例参数',
  `feature` varchar(512) NOT NULL DEFAULT '' COMMENT '扩展字段',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '活动状态：0-暂停 1-可用  2-逻辑删除 3-过期',
  `activity_source` varchar(32) NOT NULL COMMENT '活动来源标识，如应用名',
  `creator` varchar(32) NOT NULL DEFAULT '' COMMENT '创建人',
  `business_type` int(8) NOT NULL DEFAULT '0' COMMENT '业务活动类型',
  `scope_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT '活动适用范围',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '开始时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '结束时间',
  PRIMARY KEY (`id`) USING BTREE,
  KEY `idx_template` (`template_id`) USING BTREE,
  KEY `auto_shard_key_shop_id` (`shop_id`) USING BTREE,
  KEY `idx_act` (`id`,`template_id`,`shop_id`) USING BTREE,
  KEY `idx_create_time` (`create_time`),
  KEY `idx_update_time` (`update_time`),
  KEY `idx_end_time` (`end_time`) USING BTREE,
  KEY `code_shop_status` (`template_code`,`shop_id`,`status`) USING BTREE COMMENT '编码店铺状态联合索引'
) ENGINE=InnoDB AUTO_INCREMENT=1603772 DEFAULT CHARSET=utf8mb4 COMMENT='促销活动表'  dbpartition by UNI_HASH(`shop_id`) tbpartition by UNI_HASH(`shop_id`) tbpartitions 8;




CREATE TABLE `p_activity_detail` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT BY GROUP COMMENT '主键',
  `activity_id` bigint(20) NOT NULL COMMENT '活动id，一个活动可有多个详情',
  `template_id` bigint(20) NOT NULL COMMENT '模板id',
  `template_type` tinyint(4) NOT NULL COMMENT '模板类型',
  `template_code` varchar(128) NOT NULL COMMENT '模板code',
  `participate_type` tinyint(4) NOT NULL COMMENT '参与者类型，0--平台 1-商品 2-类目  3-店铺 8-分组',
  `participate_id` bigint(20) NOT NULL COMMENT '参与者id：',
  `shop_id` bigint(20) NOT NULL DEFAULT '0' COMMENT '商家id',
  `start_time` datetime NOT NULL COMMENT '活动开始时间，冗余活动时间',
  `end_time` datetime NOT NULL COMMENT '活动结束时间，冗余活动时间',
  `priority` tinyint(4) NOT NULL COMMENT '详情优先级别，同活动条件满足下优先使用级别高的详情。',
  `operation_param` varchar(2000) NOT NULL DEFAULT '' COMMENT '详情特有实例参数，其余在活动字段上。',
  `brokerage` int(11) NOT NULL DEFAULT '0' COMMENT '比例',
  `channel` tinyint(4) NOT NULL DEFAULT '1' COMMENT '适用渠道：1-环球捕手/斑马 2-本地生活 3-小区乐 4-美食买手',
  `feature` varchar(512) NOT NULL DEFAULT '' COMMENT '扩展字段',
  `status` tinyint(11) NOT NULL DEFAULT '1' COMMENT '状态：1-可用  2-逻辑删除 3-过期',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `unique_idx_app` (`activity_id`,`participate_id`,`priority`,`participate_type`) USING BTREE,
  KEY `auto_shard_key_shop_id` (`shop_id`) USING BTREE,
  KEY `idx_create_time` (`create_time`),
  KEY `idx_update_time` (`update_time`),
  KEY `idx_m_detail` (`shop_id`,`participate_id`,`participate_type`,`activity_id`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=12917180 DEFAULT CHARSET=utf8mb4 COMMENT='促销详情表'  dbpartition by UNI_HASH(`shop_id`) tbpartition by UNI_HASH(`shop_id`) tbpartitions 64;


CREATE TABLE `p_coupon` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT BY GROUP COMMENT ' 主键id',
  `activity_id` bigint(20) NOT NULL COMMENT '活动id',
  `shop_id` bigint(20) NOT NULL DEFAULT '0' COMMENT '店铺id',
  `coupon_type` tinyint(4) NOT NULL COMMENT '优惠券类型: 0-平台优惠券 1-全店优惠券 2-部分商品优惠券  ',
  `name` varchar(32) NOT NULL COMMENT '优惠券名称（店铺优惠券-优惠券名称即活动名称）',
  `extend_type` tinyint(4) NOT NULL DEFAULT '2' COMMENT '推广方式： 1-全网推广  2-自行推广',
  `threshold` int(11) NOT NULL COMMENT '使用门槛(分/件)',
  `preferential_type` tinyint(4) NOT NULL COMMENT '优惠类型：1-满减  2-无门槛满减    3-打折',
  `preferential_detail` int(11) NOT NULL COMMENT '优惠详情：优惠金额（单位分），打折时为折扣',
  `upper_limit` int(11) NOT NULL DEFAULT '0' COMMENT '优惠上限',
  `preferential_content` varchar(32) NOT NULL COMMENT '优惠内容：例如：满100减30',
  `validity_days_type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '有效期类型：1-固定有效期 2-顺延N日(领取日)',
  `days` int(11) NOT NULL DEFAULT '0' COMMENT '顺延天数',
  `start_time` datetime NOT NULL COMMENT '优惠券有效开始时间',
  `end_time` datetime NOT NULL COMMENT '优惠券有效结束时间',
  `send_start_time` datetime NOT NULL COMMENT '优惠券发放开始时间',
  `send_end_time` datetime NOT NULL COMMENT '优惠券发放结束时间',
  `terminal_type` tinyint(4) NOT NULL COMMENT '面向终端:  0-全终端 1-App专享',
  `app_code` tinyint(4) NOT NULL DEFAULT '0' COMMENT 'app版本：0-全部 ',
  `is_member` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否会员券：0-否 1-是',
  `is_new_person` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否新人券：0-否 1-是',
  `is_new_member` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否新会员：0-否 1-是',
  `use_mode` tinyint(4) NOT NULL DEFAULT '1' COMMENT '使用方式：1-用户领取 2-兑换 3-赔付 4-特殊业务专用',
  `description` varchar(32) NOT NULL DEFAULT '' COMMENT '适用类型文案',
  `limit_type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '单人限领类型：1-单人限领规则 2-单人单日限领规则',
  `limit_num` int(11) NOT NULL COMMENT '限领张数',
  `source` tinyint(4) NOT NULL DEFAULT '1' COMMENT '优惠券来源：1-促销后台  2-店铺后台 3-同步招商',
  `channel` tinyint(4) NOT NULL DEFAULT '1' COMMENT '适用渠道：1-环球捕手/斑马 2-本地生活 3-小区乐 4-美食买手',
  `feature` varchar(512) NOT NULL DEFAULT '' COMMENT '扩展字段',
  `creator` varchar(32) NOT NULL COMMENT '创建人',
  `status` tinyint(4) NOT NULL DEFAULT '1' COMMENT '优惠券状态：0-暂停 1-启用  2-逻辑删除',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `is_large_promotion` tinyint(3) NOT NULL DEFAULT '0' COMMENT '大促优惠券标识,1是0否',
  `coupon_activity_id` bigint(20) DEFAULT NULL COMMENT '优惠券活动ID',
  PRIMARY KEY (`id`),
  KEY `idx_shop_mode` (`shop_id`,`use_mode`) USING BTREE,
  KEY `idx_activity` (`shop_id`,`activity_id`) USING BTREE,
  KEY `idx_create_time` (`create_time`),
  KEY `idx_update_time` (`update_time`),
  KEY `idx_shop_extend_send_start_time` (`shop_id`,`extend_type`,`send_start_time`),
  KEY `idx_shop_coupon_activity` (`shop_id`,`coupon_activity_id`)
) ENGINE=InnoDB AUTO_INCREMENT=848104 DEFAULT CHARSET=utf8mb4 COMMENT='优惠券'  dbpartition by UNI_HASH(`shop_id`) tbpartition by UNI_HASH(`shop_id`) tbpartitions 4;



CREATE TABLE `p_buyer_resource` (
